import React, { useState } from "react";
import "./AddJob.css"; // Import your CSS file

const AddJob = () => {
  const [yearOfPassout, setYearOfPassout] = useState("");
  const [skills, setSkills] = useState("");
  const [description, setDescription] = useState("");
  const [isFresher, setIsFresher] = useState(true);
  const [resume, setResume] = useState(null);

  const [name, setName] = useState("");
  const [phno, setPhno] = useState("");
  const [email, setEmail] = useState("");
  const [languages, setLanguages] = useState("");
  const [dob, setDob] = useState("");

  const [hasExperience, setHasExperience] = useState(false);
  const [roleName, setRoleName] = useState("");
  const [company, setCompany] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const jobApplication = {
      yearOfPassout,
      skills: skills.split(",").map((skill) => skill.trim()),
      description,
      isFresher,
      resume,
      candidateDetails: {
        name,
        phno,
        email,
        languages: languages.split(",").map((lang) => lang.trim()),
        dob,
      },
      experienceDetails: hasExperience ? { roleName, company } : null,
    };

    console.log(jobApplication);

    fetch(`${process.env.REACT_APP_URL}/post`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(jobApplication),
    })
      .then((response) => response.json())
      .then((data) => console.log("Success:", data))
      .catch((error) => console.error("Error:", error));

    // Clear input fields after submission
    setYearOfPassout("");
    setSkills("");
    setDescription("");
    setIsFresher(true);
    setResume(null);
    setName("");
    setPhno("");
    setEmail("");
    setLanguages("");
    setDob("");
    setHasExperience(false);
    setRoleName("");
    setCompany("");
  };

  return (
    <>
      <p className="heading" style={{ marginBottom: "0px", fontSize: "45px" }}>
        Fill in the Job Application
      </p>
      <div className="job-form">
        <form onSubmit={handleSubmit}>
          <div className="form-section">
            <h2>Job Requirements</h2>
            <label htmlFor="yearOfPassout" className="label-input label">Year of Passout</label>
            <input
            id="yearOfPassout"
              className="form-input"
              type="text"
              placeholder="Year of Passout"
              value={yearOfPassout}
              onChange={(e) => setYearOfPassout(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Skills (comma separated)"
              value={skills}
              onChange={(e) => setSkills(e.target.value)}
              required
            />
            <textarea
              placeholder="Describe about yourself"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
            />
            <div className="radio-container">
              <label className="radio">
                <input
                  type="radio"
                  checked={isFresher}
                  onChange={() => setIsFresher(true)}
                />
                Fresher
              </label>
              <label className="radio">
                <input
                  type="radio"
                  checked={!isFresher}
                  onChange={() => setIsFresher(false)}
                />
                Experienced
              </label>
            </div>
            <div className="file-input">
              <label htmlFor="resume">Resume:</label>
              <input
                id="resume"
                type="file"
                onChange={(e) => setResume(e.target.files[0])}
                required
              />
            </div>
          </div>
          <div className="form-section">
            <h2>Candidate Details</h2>
            <input
              type="text"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Phone Number"
              value={phno}
              onChange={(e) => setPhno(e.target.value)}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="text"
              placeholder="Languages Known (comma separated)"
              value={languages}
              onChange={(e) => setLanguages(e.target.value)}
              required
            />
            <input
              type="date"
              value={dob}
              onChange={(e) => setDob(e.target.value)}
              required
            />
          </div>
          <div className="form-section">
            <h2>Experience Details</h2>
            <label className="checked">
              <input
                type="checkbox"
                checked={hasExperience}
                onChange={(e) => setHasExperience(e.target.checked)}
              />
              Have Experience
            </label>
            {hasExperience && (
              <>
                <input
                  type="text"
                  placeholder="Role Name"
                  value={roleName}
                  onChange={(e) => setRoleName(e.target.value)}
                  required={hasExperience}
                />
                <input
                  type="text"
                  placeholder="Company"
                  value={company}
                  onChange={(e) => setCompany(e.target.value)}
                  required={hasExperience}
                />
              </>
            )}
          </div>
          <button type="submit">Submit Application</button>
        </form>
      </div>
    </>
  );
};

export default AddJob;
